/* navovers.js -- A script for handling 
   navigation tabs in SWAT. */


// used in links generated by swat.c
confOver = new Image
confLink = new Image
servicesOver = new Image
servicesLink = new Image
statusOver = new Image
statusLink = new Image
passwdOver = new Image
passwdLink = new Image

confOver.src = '/swat/images/conf_over.png'  
confLink.src = '/swat/images/conf_link.png'
servicesOver.src = '/swat/images/services_over.png'  
servicesLink.src = '/swat/images/services_link.png'
statusOver.src = '/swat/images/status_over.png'  
statusLink.src = '/swat/images/status_link.png'
passwdOver.src = '/swat/images/passwd_over.png'  
passwdLink.src = '/swat/images/passwd_link.png'


// used in header.html
helpOver = new Image
helpLink = new Image

helpOver.src = '/swat/images/question_over.png'
helpLink.src = '/swat/images/question.png'


function swapImg(oldImg,newImg) {
    document[oldImg].src = eval(newImg + '.src')
}
